DROP TABLE IF EXISTS `#__etherpad_docs`;CREATE TABLE IF NOT EXISTS `#__etherpad_docs` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `user_id` int(11) NOT NULL,  `padName` varchar(255) NOT NULL,  `text` text NOT NULL,  `groupID` varchar(100) NOT NULL,  `padID` varchar(100) NOT NULL,  `date_created` varchar(25) NOT NULL,  `status` int(11) NOT NULL,  `response` text NOT NULL,  PRIMARY KEY (`id`))DEFAULT COLLATE=utf8mb4_unicode_ci;DROP TABLE IF EXISTS `#__etherpad_groups`;CREATE TABLE IF NOT EXISTS `#__etherpad_groups` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `groupMapper` int(11) NOT NULL,  `groupID` varchar(100) NOT NULL,  `date_created` varchar(25) NOT NULL,  `response` text NOT NULL,  PRIMARY KEY (`id`))DEFAULT COLLATE=utf8mb4_unicode_ci;DROP TABLE IF EXISTS `#__etherpad_users`;CREATE TABLE IF NOT EXISTS `#__etherpad_users` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `name` varchar(100) NOT NULL,  `authorMapper` int(11) NOT NULL,  `authorID` varchar(100) NOT NULL,  `date_created` varchar(25) NOT NULL,  `response` text NOT NULL,  PRIMARY KEY (`id`))DEFAULT COLLATE=utf8mb4_unicode_ci;